mkdir -p $ogredeps_dir
cd $ogredeps_dir
cmake -DCMAKE_BUILD_TYPE=Release $ogredeps_src
make install

OGRE_CMAKE_OPTIONS="-DOGRE_DEPENDENCIES_DIR=$ogredeps_dir/ogredeps"
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_RENDERSYSTEM_D3D9="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_COMPONENT_PAGING:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_COMPONENT_PROPERTY:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_COMPONENT_RTSHADERSYSTEM:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_TOOLS:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_INSTALL_TOOLS:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_COMPONENT_TERRAIN:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_PLUGIN_PFX:BOOL="1"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_PLUGIN_PCZ:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_PLUGIN_BSP:BOOL="0"'
OGRE_CMAKE_OPTIONS=$OGRE_CMAKE_OPTIONS' -DOGRE_BUILD_SAMPLES:BOOL="0"'

mkdir -p $ogre_dir
cd $ogre_dir
echo cmake -DCMAKE_BUILD_TYPE=MinSizeRel $OGRE_CMAKE_OPTIONS $ogre_src
cmake -DCMAKE_BUILD_TYPE=MinSizeRel $OGRE_CMAKE_OPTIONS $ogre_src
make
cp -rv $ogre_dir/lib/* $__build
cp -rv $ogre_dir/bin/* $__build

